INSERT INTO `mg_brand-logo` (`id`, `brand`, `url`, `desc`, `sort`, `seo_title`, `seo_keywords`, `seo_desc`) VALUES
(1, 'Outventure', 'http://faynashuna.loc/uploads/goodyear-prem-logo.jpg', '<p>\n	Открывая для себя мир Outventure, вы открываете карту с тысячей маршрутов. Бескрайние просторы, уходящие за горизонт, горные хребты, скрывающиеся в облаках, ручьи и бурные горные реки. Нас влекут неизведанные тропы и новые маршруты. Жизнь - это путешествие, и оно начинается прямо сейчас.&nbsp;\n</p>\n<br />\n<a href=\"http://www.outventure.ru/\" target=\"_blank\">www.outventure.ru</a>', 0, 'Outventure', '', 'Открывая для себя мир Outventure, вы открываете карту с тысячей маршрутов. Бескрайние просторы, уходящие за горизонт, горные хребты, скрывающиеся в облаках, ручьи и бурные горные реки'),
(2, 'Columbia', 'http://faynashuna.loc/uploads/Columbia.svg', 'Компания Columbia была основана в США в 1938 году и является на сегодняшний день одним из ведущих мировых производителей одежды, обуви и аксессуаров для туризма и активного отдыха. Также компания производит одежду для занятий горнолыжным спортом и туристический инвентарь: палатки и рюкзаки. Вся продукция этого бренда отличается высоким качеством и отличной функциональностью благодаря технологичным тканям и материалам. Благодаря использованию запатентованных технологий изделия Columbia обеспечивают своему владельцу высокий уровень комфорта в любых условиях: в жару они дарят прохладу и сухость, в непогоду — защищают от дождя и ветра, в холод — эффективно сохраняют тепло.&nbsp;<br />\n<br />\nНа российском рынке торговая марка Columbia представлена с 1998 года и уже заслужила известность и любовь среди наших потребителей. Компания «Спортмастер» является официальным эксклюзивным дистрибьютором продукции компании Columbia в России.&nbsp;<br />\n<a href=\"http://www.columbia.ru/\" target=\"_blank\">www.columbia.ru</a>', 2, 'Columbia', '', 'Компания Columbia была основана в США в 1938 году и является на сегодняшний день одним из ведущих мировых производителей одежды, обуви и аксессуаров для туризма и активного отдыха'),
(3, 'Demix', 'http://faynashuna.loc/uploads/Demix.svg', 'Жизнь – это возможность. Возможность стать лучше. И ничто не убеждает в этом сильнее, чем реальные достижения. Мы не говорим о мировых рекордах. Мы говорим о том, как спорт ежедневно меняет нашу жизнь.&nbsp;<br />\n<br />\nЗабудь про отговорки. Всё, что нужно, - это твоя воля и удобная форма. Demix - это сочетание высокой функциональности и бескомпромиссного качества. Demix - это не просто технологии. Наши одежда, обувь и аксессуары помогают добиваться результатов. Тех результатов, которые нужны именно тебе.&nbsp;<br />\n<br />\nDemix.<br />\n<a href=\"http://www.demix.ru/\" target=\"_blank\">www.demix.ru</a>', 3, 'Demix', '', 'Жизнь – это возможность. Возможность стать лучше. И ничто не убеждает в этом сильнее, чем реальные достижения. Мы не говорим о мировых рекордах. Мы говорим о том, как спорт ежедневно меняет нашу жизнь'),
(4, 'Nike', 'http://faynashuna.loc/uploads/Nike.svg', 'Легендарная одежда и обувь Nike — ваш шаг на пути к спортивному успеху. Благодаря сочетанию функциональности, современного дизайна и высоких технологий одежда «от Найк» является символом качества для нескольких поколений. В производстве этой одежды применяются самые последние технологии, позволяющие ей обеспечивать максимальный комфорт и удобство при занятиях спортом.<br />\n<br />\nDri-FIT — одежда для интенсивных тренировок. Она «умеет» удалять пот с поверхности тела, не впитывая его и не становясь тяжелее.<br />\n<br />\nTherma-FIT — непроницаемая для ветра и отлично сохраняющая тепло ткань, которая отлично подходит для занятий на открытом воздухе. Одежда из этой ткани очень мало весит и в то же время не даёт вам замерзнуть.<br />\n<br />\nClima-FIT — инновационная ткань, которая не пропускает воду и в то же время позволяет коже дышать.<br />\n<br />\nУдобная обувь во время занятий спортом не менее важна, чем одежда. Женская, мужская и детская обувь Nike — это стильный внешний вид, поддержка стопы и посадка точно «по ноге», гарантирующие удобство и безопасность. Её не случайно называют самой надёжной спортивной обувью в мире. Так, модели кроссовок, созданные по технологии Impax, отличаются от других очень небольшим весом и абсолютной амортизацией — то есть смягчают удары и отдачу во время прыжка. За счёт технологии Air MAX — воздушных подушек, расположенных по краям подошвы, кроссовки Nike значительно снижают нагрузку на позвоночник во время бега. Кроссовки этой марки подходят и для повседневной носки, что делает их очень — очень популярными.', 4, 'Nike', '', 'Легендарная одежда и обувь Nike — ваш шаг на пути к спортивному успеху. Благодаря сочетанию функциональности, современного дизайна и высоких технологий одежда «от Найк» является символом качества для нескольких поколений'),
(5, 'TORNEO', 'http://faynashuna.loc/uploads/Torneo.svg', 'Чтобы заниматься фитнесом не нужно много денег и совсем не обязательно записываться в фитнесс-клуб, покупая дорогой абонемент.<br />\nКардио-тренировки, силовые тренировки, фитнес, спорт с ракеткой — это все доступно для людей, выбирающих продукцию Torneo, сочетающую в себе демократичные цены, богатый функционал и надежность продукции.<br />\nВ ассортименте есть все необходимое для занятий дома и на свежем воздухе: от многофункциональных тренажеров до аксессуаров, продукции для ракеточных видов спорта и активных игр.<br />\n<br />\nПри разработке продукции Torneo используются технические решения, делающие товары удобнее, надежнее и эффективнее. Также учитываются современные тенденции дизайна (над каждой линейкой тренажеров работают опытные итальянские дизайнеры), что позволяет легко вписать их в любой интерьер.&nbsp;<br />\n<br />\nПроизводство размещено на заводах, производящих товары для домашнего и клубного фитнеса ведущих мировых брендов и соответствует строгим требованиям. QCT (Quality Control Team)* осуществляет полный контроль качества, начиная с образца и заканчивая отгружаемым товаром.&nbsp;<br />\n<br />\nTorneo делает спорт доступным потребителю с любым уровнем дохода, благодаря оптимизации конструкции и материалов, но без ущерба функциональности, качеству, а главное, здоровью – чтобы ничто не стояло на пути к достижению фитнесс-целей!&nbsp;<br />\n<a href=\"http://www.torneo.ru/\" target=\"_blank\">www.torneo.ru</a>', 5, 'TORNEO', '', 'Чтобы заниматься фитнесом не нужно много денег и совсем не обязательно записываться в фитнесс-клуб, покупая дорогой абонемент. Кардио-тренировки, силовые тренировки, фитнес, спорт с ракеткой — это все доступно для людей, выбирающих продукцию Torneo, сочетающую в себе демократичные цены, богатый функционал и надежность продукции'),
(6, 'TERMIT', 'http://faynashuna.loc/uploads/Termit.svg', 'Под брендом Termit выпускается стильная одежда и обувь для таких видов спорта, как сноубординг и горные лыжи и плавание, а также для повседневного использования. Широко представлены аксессуары — от головных уборов и перчаток до рюкзаков и пляжных сумок. Товары Termit отличаются яркими цветами, удобством и качеством исполнения используемых материалов.', 7, 'TERMIT', '', 'Под брендом Termit выпускается стильная одежда и обувь для таких видов спорта, как сноубординг и горные лыжи и плавание, а также для повседневного использования'),
(7, 'SKECHERS', 'http://faynashuna.loc/uploads/Skechers.svg', 'Компания&nbsp;<b>Skechers</b>, мировой лидер молодежной обувной индустрии, основана в 1992 г. и состоит из нескольких самостоятельных брендов. Основное направление деятельности компании, прославившее её, — модная, современная и функциональная обувь «на каждый день».&nbsp;<br />\n<a href=\"http://www.skechers.ru/\" target=\"_blank\">www.skechers.ru</a>', 8, 'SKECHERS', '', 'КомпанияSkechers, мировой лидер молодежной обувной индустрии, основана в 1992 г. и состоит из нескольких самостоятельных брендов. Основное направление деятельности компании, прославившее её, — модная, современная и функциональная обувь «на каждый день»'),
(8, 'Apple', 'http://faynashuna.loc/uploads/Apple_with_wordmark.svg', '<p>\n	<b>Apple</b>&nbsp;(рус.&nbsp;<i>Эппл</i>)—&nbsp;американская&nbsp;корпорация, производитель&nbsp;персональных&nbsp;и&nbsp;планшетных&nbsp;компьютеров,&nbsp;аудиоплееров,&nbsp;телефонов,&nbsp;программного обеспечения. Один из пионеров в области персональных компьютеров<span style=\"font-size: 10.8333px;\"> </span>и современных многозадачных&nbsp;операционных систем&nbsp;с графическим интерфейсом. Штаб-квартира&nbsp;— в&nbsp;Купертино, штат&nbsp;Калифорния.\n</p>\n\n<p>\n	Благодаря инновационным технологиям и эстетичному дизайну, корпорация Apple создала уникальную репутацию, сравнимую с культом, в индустрии потребительской электроники.&nbsp;Компания занимает первое место в мире по&nbsp;рыночной капитализации, размер которой по состоянию на 5 сентября 2017 года составляет $850 млрд.\n</p>\n', 6, 'Apple', '', 'Apple(рус.Эппл)—американскаякорпорация, производительперсональныхипланшетныхкомпьютеров,аудиоплееров,телефонов,программного обеспечения. Один из пионеров в области персональных компьютеров и современных многозадачныхоперационных системс графическим интерфейсом');
