INSERT INTO `mg_page` (`id`, `parent_url`, `parent`, `title`, `url`, `html_content`, `meta_title`, `meta_keywords`, `meta_desc`, `sort`, `print_in_menu`, `invisible`) VALUES
(1, '', 0, 'Главная', 'index', '<h3 style=\"text-align: center;\">\n	<strong>Добро пожаловать в наш интернет-магазин!</strong>\n</h3>\n&nbsp;\n\n<div>\n	<p>\n		Мы стабильная и надежная компания, с каждым днем наращиваем свой потенциал. Имеем огромный опыт в сфере корпоративных продаж, наши менеджеры готовы предложить Вам высокий уровень сервиса, грамотную консультацию, выгодные условия работы и широкий спектр цветовых решений. В число наших постоянных клиентов входят крупные компании.\n	</p>\n\n	<p>\n		Наши товары производятся только из самых качественных материалов!\n	</p>\n\n	<p>\n		Отдел корпоративных продаж готов предложить Вам персонального менеджера, грамотную консультацию, доставку на следующий день после оплаты, сертификаты на всю продукцию, индивидуальный метод работы.\n	</p>\n\n	<p>\n		Отдельным направлением является работа с частными лицами с оперативной доставкой, низкими ценами и высоким качеством обслуживания.\n	</p>\n\n	<p>\n		Главное для нас — своевременно удовлетворять потребности наших клиентов всеми силами и доступными нам средствами. Работая с нами, Вы гарантированно приобретаете только оригинальный товар подлинного качества.\n	</p>\n\n	<p>\n		Мы работаем по всем видам оплат. Только приобретая товар у официального дилера, Вы застрахованы от подделок. Будем рады нашему долгосрочному сотрудничеству.\n	</p>\n\n	<p>\n		** Информация представленная на сайте является демонстрационной для ознакомления с Moguta.CMS. <a href=\"https://moguta.ru/\">Moguta.CMS - простая cms для интернет-магазина.</a>\n	</p>\n</div>\n', 'Главная', 'Главная', 'Autoshini Сom – купить зимние шины и летние автошины, литые и стальные диски. Резина и покрышки в интернет-магазине', 5, 0, 1),
(2, '', 0, 'Доставка и оплата', 'dostavka', '<div><h1 class=\"new-products-title\">Доставка и оплата</h1><p><strong>Курьером по Москве</strong></p><p>Доставка осуществляется по Москве бесплатно, если сумма заказа составляет свыше 3000 руб.  Стоимость доставки меньше чем на сумму 3000 <span style=\"font-size: 12px;\">руб</span>. Составляет 700 руб. Данный способ доставки дает вам возможность получить товар прямо в руки, курьером по <span style=\"font-size: 12px;\">Москве </span>. Срок доставки до 24 часов с момента заказа товара в интернет - магазине.</p><p><strong>Доставка по России</strong></p><p>Доставка по <span style=\"font-size: 12px;\">России</span>осуществляется с помощью почтово – курьерских служб во все регионы <span style=\"font-size: 12px;\">России</span>. Стоимость доставки зависит от региона и параметров товара. Рассчитать стоимость доставки Вы сможете на официальном сайте почтово – курьерской службы Почта-<span style=\"font-size: 12px;\">России</span> и т.д. Сроки доставки составляет до 3-х дней с момента заказа товара в интернет – магазине.</p><h2>Способы оплаты:</h2><p><strong>Наличными: </strong>Оплатить заказ товара Вы сможете непосредственно курьеру в руки при получение товара. </p><p><strong>Наложенным платежом:</strong> Оплатить заказ товара Вы сможете наложенным платежом при получение товара на складе. С данным видом оплаты Вы оплачиваете комиссию за пересылку денежных средств. </p><p><strong>Электронными деньгами:</strong> VISA, Master Card, Yandex.Деньги, Webmoney, Qiwi и др.</p></div><div></div><div></div><div></div>', 'Доставка', 'Доставка', 'Доставка осуществляется по Москве бесплатно, если сумма заказа составляет свыше 3000 руб.  Стоимость доставки меньше чем на сумму 3000 руб. Составляет 700 руб.', 2, 1, 0),
(3, '', 0, 'Обратная связь', 'feedback', '<p>Свяжитесь с нами, посредством формы обратной связи представленной ниже. Вы можете задать любой вопрос, и после отправки сообщения наш менеджер свяжется с вами.</p>', 'Обратная связь', 'Обратная связь', 'Свяжитесь с нами, по средствам формы обратной связи представленной ниже. Вы можете задать любой вопрос, и после отправки сообщения наш менеджер свяжется с вами.', 3, 1, 0),
(4, '', 0, 'Контакты', 'contacts', '<h1 class=\"new-products-title\">Контакты</h1><p><strong>Наш адрес </strong>г. Санкт-Петербург Невский проспект, дом 3</p><p><strong>Телефон отдела продаж </strong>8 (555) 555-55-55 </p><p>Пн-Пт 9.00 - 19.00</p><p>Электронный ящик: <span style=\"line-height: 1.6em;\">info@sale.ru</span></p><p><strong>Мы в социальных сетях</strong></p><p></p><p style=\"line-height: 20.7999992370605px;\"><strong>Мы в youtoube</strong></p><p style=\"line-height: 20.7999992370605px;\"></p>', 'Контакты', 'Контакты', 'Мы в социальных сетях  Мы в youtoube ', 4, 1, 0),
(5, '', 0, 'Каталог', 'catalog', 'В каталоге нашего магазина вы найдете не только качественные и полезные вещи, но и абсолютно уникальные новинки из мира цифровой индустрии.', 'Каталог', 'Каталог', 'Подбор дисков по марке авто. Подбор стальнымх и литых по марке автомобиля', 1, 1, 0),
(7, '', 0, 'Акция! Скидка на обувь', 'aktsiya-skidka-na-obuv', '<h2 style=\"text-align: center;\">\n	Невероятная акция!\n</h2>\n&nbsp;\n\n<p style=\"text-align: center;\">\n	Только до конца октября в честь открытия нашего магазина мы предоставляем скидку 20% на весь ассортимент обуви!<br />\n	&nbsp;\n</p>\n\n<div style=\"text-align: center;\">\n	<strong><span style=\"font-size:24px;\">Промокод - NEWSHOP - Скидка 20%</span></strong>\n</div>\n', '', '', '', 7, 0, 1),
(8, '', 0, 'Акция! Скидка 26% на весь ассортимент товаров', 'aktsiya-skidka-26-na-ves-assortiment-tovarov', '<h2 style=\"text-align: center;\">\n	А у нас очередная акция!\n</h2>\n\n<div style=\"text-align: center;\">\n	&nbsp;\n</div>\n\n<p style=\"text-align: center;\">\n	<span style=\"font-size:14px;\">Снижаем цены на 26% на весь ассортимент в октябре!</span>\n</p>\n\n<div style=\"text-align: center;\">\n	<br />\n	<span style=\"font-size:26px;\"><strong>Промокод - LUCKYSALE</strong><br />\n	&nbsp;успейте получить скидку в 26% на любой товар!</span>\n</div>\n', '', '', '', 8, 0, 1),
